mapper.select:
SELECT room_member_balance
FROM roommembers
WHERE member_id = #{memberId}
AND room_id = #{roomId}

SELECT room_member_balance
FROM roommembers
WHERE room_member_id = #{roomMemberId}

SELECT room_member_id
FROM roommembers
WHERE member_id = #{memberId}
  AND room_id = #{roomId}

mapper.update:
UPDATE roommembers
SET room_member_balance = #{roomMemberBalance}
WHERE room_member_id = #{roomMemberId}
